import React, {Component} from 'react';
    import {toast} from 'react-toastify'; 
    import 'react-toastify/dist/ReactToastify.css';
    import axios from 'axios';
import { APIData } from '../Authentication/APIData';
    toast.configure();
    const initialState = {

    }
      const sessiondetails=JSON.parse(localStorage.getItem("sessiondetails"));
      class StudentProfile extends Component {
            constructor(props) {
                  super(props)
                  this.state = {
                        student_id: "",
                        student_id_error: "",
                        name: '',
                        name_error: '',
                        email: "",
                        email_error: "",
                        phone_number: '',
                        phone_number_error: '',
                        fathers_name: "",
                        fathers_name_error: "",
                        mother_name: '',
                        mother_name_error: '',
                        address: "",
                        address_error: "",
                        gender: '',
                        gender_error: '',
                        alt_number: '',
                        alt_number_error: '',
                        institution: '',
                        institution_error: '',
                        educational_qualification: '',
                        educational_qualification_error: '',
                        professional_exp: '',
                        professional_exp_error: '',
                        dob: '',
                        dob_error: ''
                        
                  };
              }





              async componentDidMount(){
                  const url =  APIData.api+"students/" +sessiondetails.user;
                  axios.get(url,{headers:APIData.headers})
                    .then(response => {
                        if(response.status==200){
                      this.setState({
                          student_id: response.data.student_id,
                          name: response.data.name,
                          email: response.data.email,
                          phone_number: response.data.phone_number,
                          mother_name: response.data.mother_name,
                          address: response.data.address,
                          gender: response.data.gender,
                          alt_number: response.data.alt_number,
                          institution: response.data.institution,
                          educational_qualification: response.data.educational_qualification,
                          professional_exp: response.data.professional_exp,
                          fathers_name: response.data.fathers_name,
                          dob: response.data.dob
                      });}
          
                    })
                    .catch(error => {
                      toast('Its Time to grab a coffee')
                    })
                  
              }
                 
          
              validation = () =>{
              let   dob_error = '';
              let    name_error = '';
              let   email_error = '';
              let     phone_number_error = ''
              let   fathers_name_error = '';
              let   mother_name_error = '';
              let   address_error = '';
              let   gender_error = '';
              let   alt_number_error = '';
              let   institution_error = '';
              let   educational_qualification_error = '';
              let   professional_exp_error = '';
             
              if(!this.state.name){
              name_error = "Invalid Name";
              }
          
              if(!this.state.email.includes('@')){
              email_error="Invalid Email!";
              }
              if(!this.state.phone_number){
              phone_number_error = "Invalid Phone Number";
              }
              if(!this.state.fathers_name){
                fathers_name_error = "Invalid Phone Number";
                }
                if(!this.state.mother_name){
                  mother_name_error = "Invalid Phone Number";
                  }
                  if(!this.state.address){
                    address_error = "Invalid Phone Number";
                    }
                    if(!this.state.gender){
                      gender_error = "Invalid Phone Number";
                      }
                      if(!this.state.alt_number){
                        alt_number_error = "Invalid Phone Number";
                        }
                        if(!this.state.institution){
                          institution_error = "Invalid Phone Number";
                          }
                          if(!this.state.educational_qualification){
                            educational_qualification_error = "Invalid Phone Number";
                            }
                            if(!this.state.professional_exp){
                              professional_exp_error = "Invalid Phone Number";
                              }
                              if(!this.state.dob){
                                dob_error = "Invalid Phone Number";
                                }
              if(name_error || email_error || phone_number_error||fathers_name_error||mother_name_error||address_error||gender_error||alt_number_error||institution_error||educational_qualification_error||professional_exp_error||dob_error ){
              this.setState({name_error , email_error , phone_number_error,fathers_name_error,mother_name_error,address_error,gender_error,alt_number_error,institution_error,educational_qualification_error,professional_exp_error,dob_error});
              return false;
              }
              return true;
              }
              handleChange = event => {
          
              const isCheckbox = event.target.type === "checkbox";
              this.setState({
              [event.target.userEmailError]: isCheckbox
              ? event.target.checked
              : event.target.value
              });
              };
          
          
              changeHandler = e => {
              this.setState({ [e.target.name]: e.target.value })
              }
          
              submitHandler = e => {
              e.preventDefault()
          
          
              var sendstate = {
                student_id: sessiondetails.user,
                name: this.state.name,
               email: this.state.email,
              phone_number: this.state.phone_number,
              fathers_name: this.state.fathers_name,
              mother_name: this.state.mother_name,
              address: this.state.address,
              gender: this.state.gender,
              alt_number: this.state.alt_number,
              institution: this.state.institution,
              educational_qualification: this.state.educational_qualification,
              professional_exp: this.state.professional_exp,
              dob: this.state.dob



              }
              console.log(this.sendstate)
              axios
              .post('http://103.142.165.146:8080/smarter-learnings/students/', sendstate)
              .then(response => {
              console.log(response)
              toast(response);
              const url = new URL('http://localhost:3000');
              window.location.href = url;
              })
              .catch(error => {
              toast("registration failed");
              console.log(error)
              })
          
              const isValide = this.validation();
          
              if (isValide){
              this.setState(initialState)
              }
              if(this.state.phone_number.length != 10 && this.state.phone_number.length != 12){
          
              toast('Please enter a valid phone number',{position: toast.POSITION.TOP_RIGHT});
              }
          
              }
          


